import React,{useState} from 'react'
import { useHistory } from "react-router-dom";
import axios from 'axios';
import DashboardHeader from './DashboardHeader'
import DashboardIntro from './DashboardIntro'
import DashboardOutro from './DashboardOutro'
import ExpenseTracker from './Expence/ExpenseTracker'
import ModalExpense from './ModalExpense'
import SettleExpense from './SettleExpense'


function Dashboard(){
    const history = useHistory();

    const [view, setView] = useState([
        {
            dash:"dash-intro",
            visibility: true
        },
        {
            dash:"dash-outro",
            visibility: false
        },
        {
            dash:"dash-exp",
            visibility: false
        },
        {
            modal:false
        },
        {
            settle:false
        }
    ])
    function handleView(newView){
        setView(newView)
    }

    const[loginAuth,setLoginAuth] = useState("");
    React.useEffect(()=>{
        document.title = "Dashboard · Splitwise"
        const token = localStorage.getItem("login-key");
        // console.log(token)
        axios.post(`http://localhost:4000/check-login`,{"data":token})
          .then(res => {
            //console.log(res);
            // console.log(res.data);
            setLoginAuth(res.data)
        }); 
        if(loginAuth === "jwt expired") {
            localStorage.clear()
            history.push("/loading")
            setTimeout(()=>{
                history.push("/");
            },1700)
        }
        if(loginAuth === "jwt must be provided") {
            localStorage.clear()
            history.push("/loading")
            setTimeout(()=>{
                history.push("/");
            },1700)
        }
    },[loginAuth])

    function CustomRender(props){
        const dashCurrent = props.viewType.map(item => {
            if (item.visibility) {
                if(item.dash == "dash-intro"){
                    return <DashboardIntro key={item.dash} />
                }
                if(item.dash == "dash-outro"){
                    return  <DashboardOutro key={item.dash}/>
                }
                if(item.dash == "dash-exp"){
                    return <ExpenseTracker key={item.dash}/>
                }
            }
        })
        return dashCurrent
    }
    return (
        <>
            <div className="dashboard-content">
                <DashboardHeader method={handleView} viewType={view}/>
                <CustomRender viewType={view}/> 
            </div>
            <ModalExpense method={handleView} viewModal={view}/>
            <SettleExpense method={handleView} viewModal={view}/>
        </>
    )
}

export default Dashboard
